	public static function Peidui($cid, $time, $pcid)
	{
		$db = array();
		for($i=0;$i<DATABASE_NODE_NUM;$i++){
			for($j=0;$j<=49;$j++){
				$db[$i][]= DATABASE_NODE_NUM*$j + $i;
			}
		}
		$dal = Hapyfish2_Island_Event_Dal_Peidui::getDefaultInstance();
		foreach($db as $k => $v){
			foreach($v as $k1 => $v1){
				$uidlist = $dal->getUid($v1, $cid, $time);
				if($uidlist){
					foreach($uidlist as $k2 => $v2){
						$com = new Hapyfish2_Island_Bll_Compensation();
						$com->setItem($pcid, 1);
						$ok = $com->sendOne($v2, '中秋节赠送：');
						if($ok){
							info_log($v2, 'zhongqiu_peidui'.$pcid);
						}
					}
				}
			}
		}
	}

	public static function newSend($cid, $pcid)
	{
		$db = array();
		for ($i=0; $i<DATABASE_NODE_NUM; $i++) {
			for ($j=0; $j<=49; $j++) {
				$db[$i][] = DATABASE_NODE_NUM * $j + $i;
			}
		}
		$arr = array();
		$arr1 = array();
		foreach($arr as $da){
			if(isset($arr1[$da])){
				$arr1[$da] +=1;
			}else{
				$arr1[$da] =1;
			}
		}
		foreach($db as $key => $value){
			foreach($value as $k =>$v){
				$dal = Hapyfish2_Island_Event_Dal_Peidui::getDefaultInstance();
				$data = $dal->getNewSendUid($v, $cid);
				if($data){
					foreach($data as $k1=>$v1){
						if(isset($arr1[$v1['uid']])){
							$count = $v1['num'] - $arr1[$v1['uid']];
						}else{
							$count = $v1['num'];
						}

						if ($count > 0) {
							$com = new Hapyfish2_Island_Bll_Compensation();
							$com->setItem($pcid, $count);
							$ok = $com->sendOne($v1['uid'], '中秋节赠送：');
							if($ok){
								info_log(json_encode($v1), 'zhongqiu_peidui2'.$pcid);
							}
						}
					}
				}

			}
		}


	}
	
